import React, { useState } from "react";
import axios from 'axios'
import { useHistory } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Select from 'react-select';

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];


const AddUser = () => {

  const [selectedOption, setSelectedOption] = useState(null);

  const [isVisible, setIsVisible] = useState(false)

  let history = useHistory();
  const [user, setUser] = useState({
    name: "",
    username: "",
    email: "",
    phone: "",
    website: ""
  });

  const { name, username, email, phone, website } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    await axios.post("http://localhost:3003/users", user);
    setTimeout(()=>{
      history.push("/");
    },5000)

    notify();
  };

  const notify = () =>  toast("User Succefully Added");

  return (
    <div className="container">
    <h1 style={{textAlign:'center'}}>Select Role:</h1>
					<Select
						id="reason-list"
						defaultValue={selectedOption}
						onChange={setSelectedOption}
						options={options}
					/>

<form onSubmit={e => onSubmit(e)}>

      <div className="w-75 mx-auto shadow p-5" style={{ display: isVisible ? "block" : "none" }}>

        <h2 className="text-center mb-4 p-4">Add A User</h2>        
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Name"
              name="name"
              required
              autocomplete="off"
              value={name}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Username"
              name="username"
              required
              autocomplete="off" 
              value={username}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              className="form-control form-control-lg"
              placeholder="Enter Your E-mail Address"
              name="email"
              required
              autocomplete="off"
              value={email}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Phone Number"
              name="phone"
              required
              autocomplete="off"
              value={phone}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Website Name"
              name="website"
              required
              autocomplete="off"
              value={website}
              onChange={e => onInputChange(e)}
            />
          </div>
          <ToastContainer />
      </div>
      <div className="col-md-12 text-center" style={{ paddingTop: '10px' }}>
          <button className="btn btn-primary btn-block" onClick={() => setIsVisible(true)}>Add User</button>
      </div>
      </form>
    </div>
  );
};

export default AddUser;
