import React, { Component } from 'react';
import Select from 'react-select';
import axios from 'axios';

const options = [
    { value: 'vendor', label: 'Vendor' },
    { value: 'smsrole_user', label: 'SMS ROLE USER' },
    { value: 'client', label: 'Client' },
  ];

export default class Dispatchcc extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Inception: false,
            Avatar: false,
            Parasite: false,
            selectedOption: null,
        };
      }

      onChange1 = () => {
        this.setState(res => ({
          Inception: !res.Inception,
        }));
      }
    
      onChange2 = () => {
        this.setState(res => ({
          Avatar: !res.isOrange,
        }));
      }
    
      onChange3 = () => {
        this.setState(res => ({
          Parasite: !res.isBanana,
        }));
      }

      // Submit checkbox
  onFormSubmit = (event) => {
    event.preventDefault();
    let checkboxCollection = [];
    
    for (var key in this.state) {
      if (this.state[key] === true) {
        checkboxCollection.push(key);
      }
    }

    let ifChecked = {
      checkbox: checkboxCollection.toString()
    };
    
    if(ifChecked.checkbox === "") {
       alert('Select atleast one value');
       return false;
    } else {
      axios.post('http://localhost:3003/posts', ifChecked)
      .then((response) => {
         console.log(response.data)
      }).catch((err) => {
         console.log(err)
      });
    }


  }

      //Check Checkbox Function
      handleCheckboxChange = event => {
        this.setState({ agreement: event.target.checked })
        console.log(event)
      }

      handleSubmit = e => {
        e.preventDefault();
        console.log(`checked: ${this.state.agreement}`);
      };

      //=========================================

      handleChange = (selectedOption) => {
        this.setState({ selectedOption }, () =>
          console.log(`Option selected:`, this.state.selectedOption)
        );
      };
      
    render() {
        const { selectedOption } = this.state;

        return (
            <div className="mt-4">
            <form onSubmit={this.onFormSubmit}>
            <div className="container">
                <div class="float-right"><button type="submit" class="btn btn-danger btn-lg">Generate DC</button></div><br/><br/>
            <div className="row">
                <div className="col-md-4">
                <h5><label for="exampleInputEmail1" class="form-label">PO Number</label></h5>
                <Select
                        value={selectedOption}
                        onChange={this.handleChange}
                        options={options}
                    />
                </div>
                <div className="col-md-4">
                <h5><label for="exampleInputEmail1" class="form-label">Order Number</label></h5>
                <Select
                        value={selectedOption}
                        onChange={this.handleChange}
                        options={options}
                    />
                </div>
                <div className="col-md-4">
                <h5><label for="exampleInputEmail1" class="form-label">Supplier</label></h5>
                <Select
                        value={selectedOption}
                        onChange={this.handleChange}
                        options={options}
                    />
                </div>
            </div>
            </div> 
                    <table class="table table-bordered mt-4">
                        <thead class="thead-dark">
                            <tr>
                                <th scope="col">
                                <input type="checkbox" 
                                    checked={this.state.agreement}
                                    onChange={this.handleCheckboxChange}/>
                                </th>
                                <th scope="col">IC Number</th>
                                <th scope="col">PO Number</th>
                                <th scope="col">PO Line item no</th>
                                <th scope="col">IC qty</th>
                                <th scope="col">Qty already Issued DC</th>
                                <th scope="col">Balance Qty available for DC</th>
                                <th scope="col">Present DC</th>
                                <th scope="col">Remaining</th>
                                <th scope="col">Billing schedule No</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <th scope="row"><input type="checkbox"/></th>
                                <td>123-1</td>
                                <td>10</td>
                                <td>100</td>
                                <td>0</td>
                                <td>100</td>
                                <td>50</td>
                                <td><input type="number" class="form-control" aria-describedby="present" placeholder="DC"/></td>
                                <td>50</td>
                            </tr>
                        </tbody>
                    </table>
                    </form>
                </div>
        )
    }
}
