import React, { useState } from "react";
import axios from 'axios'
import { useHistory } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Select from 'react-select';

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];


const AddUser = () => {

  const [selectedOption, setSelectedOption] = useState(null);

  const [isVisible, setIsVisible] = useState(false)

  let history = useHistory();
  const [user, setUser] = useState({
    fname: "",
    lname:"",
    fullname:"",
    email: "",
    password: "",
    state: "",
    country: "",
    address: "",
    address1: "",
    address2: "",
    vendor_number: "",
    vendor_code: "",
    sales_person: "",
    schema: "",
    supplier_category: "",
    ssitype: "",
    vendor_roles: "",
    vendor_gst: "",
    vendor_currency: "",
    vendor_type: "",
  });

  const { fname, lname, fullname, password, email,  state, country, address, address1, address2, vendor_number, vendor_code, sales_person, schema,
    supplier_category, ssitype, vendor_roles, vendor_gst, vendor_currency, vendor_type } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    await axios.post("http://localhost:3003/users", user);
    setTimeout(() => {
      history.push("/");
    }, 5000)

    notify();
  };

  const notify = () => toast("User Succefully Added");

  return (
    <div className="container" style={{ marginBottom: '3%' }}>
      <h1 style={{ textAlign: 'center' }}>Select Role:</h1>
      <Select
        id="reason-list"
        defaultValue={selectedOption}
        onChange={setSelectedOption}
        options={options}
      />

      <form onSubmit={e => onSubmit(e)}>

        <div className="w-75 mx-auto shadow p-5" style={{ display: isVisible ? "block" : "none", marginTop: "1%" }}>

          <h2 className="text-center mb-4">Add A User</h2>
          <div class="form-row">
            <div className="form-group col-md-6">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="First Name"
                name="fname"
                required
                autocomplete="off"
                value={fname}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="Last Name"
                name="lname"
                required
                autocomplete="off"
                value={lname}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="Full Name"
                name="fullname"
                required
                autocomplete="off"
                value={fullname}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="password"
                className="form-control form-control-lg"
                placeholder="Password"
                name="password"
                required
                autocomplete="off"
                value={password}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="email"
                className="form-control form-control-lg"
                placeholder="E-mail Address"
                name="email"
                required
                autocomplete="off"
                value={email}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="State"
                name="state"
                required
                autocomplete="off"
                value={state}
                onChange={e => onInputChange(e)}
              />
            </div>
            <div className="form-group col-md-6">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="Enter Your Website Name"
                name="country"
                required
                autocomplete="off"
                value={country}
                onChange={e => onInputChange(e)}
              />
            </div>
          </div>
          <ToastContainer />
        </div>
        <div className="col-md-12 text-center" style={{ paddingTop: '10px' }}>
          <button className="btn btn-primary btn-block" onClick={() => setIsVisible(true)}>Add User</button>
        </div>
      </form>
    </div>
  );
};

export default AddUser;
